mapper.select:
SELECT
admin_id as adminId,
admin_pw as adminPw
FROM admin
WHERE admin_id = #{adminId}
AND admin_pw = #{adminPw}

mapper.update:

UPDATE admin SET
admin_pw = #{adminPw},
admin_name = #{adminName}
WHERE admin_id = #{adminId}


UPDATE admin SET
admin_pw = #{adminPw}
WHERE admin_email = #{adminEmail}
