mapper.update:
UPDATE member_board
SET board_title = #{boardTitle},
	board_content = #{boardContent}
WHERE board_no = #{boardNo}
AND board_user = #{memberId}

mapper.delete:
DELETE FROM member_board
WHERE board_no = #{boardNo}
AND board_user = #{memberId}

mapper.select:
SELECT board_no as boardNo, board_title as boardTitle, board_user as boardUser, board_date as boardDate FROM member_board
board_title like CONCAT('%', #{searchWord}, '%')
ORDER BY board_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*)
FROM member_board
WHERE board_no = #{boardNo}
AND board_user = #{memberId}
