mapper.update:
UPDATE player
SET birth_date = #{birthDate}, country = #{country}, height = #{height}, weight = #{weight} , player_position = #{playerPosition}
WHERE player_no = #{playerNo};

mapper.select:
SELECT player_no as playerNo, player_name as playerName, birth_date as birthDate, country as country , height as height , weight as weight , player_position as playerPosition FROM player
player_name like CONCAT('%', #{searchWord}, '%')
ORDER BY birth_date ASC LIMIT #{beginRow} , #{rowPerPage}
