mapper.insert:
INSERT INTO refree(
refree_name,
birth_date ,
country
) VALUES (
#{refreeName} ,
#{birthDate} ,
#{country}
)

mapper.select:
SELECT refree_no as refreeNo , refree_name as refreeName , birth_date as birthDate , country as country FROM refree
refree_name like CONCAT('%', #{searchWord}, '%')
ORDER BY birth_date ASC LIMIT #{beginRow} , #{rowPerPage}
